  //   return () => {
  //     window.removeEventListener("scroll", scrollEvent);
  //   };
  // }, [scrollEvent]);

    return (
      <>
      <MediaQuery query="(max-width: 767px)"></MediaQuery>
      <MediaQuery query="(min-width: 767px)"></MediaQuery>
        <Navbar  key="md" expand="md" className="mb-3" collapseOnSelect>
          <Container>
            <Navbar.Brand href="#">
              <img src={polymath_logo} className="polymath-logo" />
            </Navbar.Brand>
            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-md`} />
            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-md`}
              aria-labelledby={`offcanvasNavbarLabel-expand-md`}
              placement="end"
            >
              <Offcanvas.Header closeButton>
                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-md`}>
                  <img src={polymath_logo} className="polymath-logo-offcanvas" />
                </Offcanvas.Title>
              </Offcanvas.Header>
              <Offcanvas.Body>
                <Nav className="justify-content-around flex-grow-1 pe-3">
                  <Nav.Link href="#home">Home</Nav.Link>
                  <Nav.Link href="#team">Team</Nav.Link>
                  <Nav.Link href="#value">Value</Nav.Link>
                  <Nav.Link href="#achievements">Achievements</Nav.Link>
                  <Nav.Link href="#contact">Contact</Nav.Link>
                  <Nav.Link href="#social">Social</Nav.Link>
                </Nav>
              </Offcanvas.Body>
            </Navbar.Offcanvas>
          </Container>
        </Navbar>
      </>
    );

}

export default BootstrapNavbar;
