import React, { useEffect, useRef, useState } from "react";
import Card from "react-bootstrap/Card";
import "./NoteTimeline.css";

function NoteTimeline() {
  const [articles, setArticles] = useState([]);
  const displayNumber = 6;

  const fetchNoteRss = () => {
    const endpointURL = "http://localhost:5000/Note";
    fetch(endpointURL)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);

        const articles = [];
        for (let i = 0; i < displayNumber; i++) {
          if (data["items"][i]) {
            articles.push({
              title: data["items"][i]["title"],
              link: data["items"][i]["link"],
              content: data["items"][i]["content"],
              thumbnailURL: data["items"][i]["media:thumbnail"],
              pubDate: data["items"][i]["pubDate"],
            });
          } else {
            console.log("doesn't exist");
          }
        }

        console.log(articles);
        console.log(articles[0]["thumbnailURL"]);

        setArticles(articles);
      });
  };

  useEffect(() => {
    fetchNoteRss();
  }, []);

  return (
    <div>
      {articles.map((article) => (
        <div>
          <Card>
            <i src= {article.thumbnailURL} className="thumbnail" />

            <Card.Body>
              <Card.Text className="title-size">
                {article.title}
              </Card.Text>
            </Card.Body>

          </Card>
        </div>
      ))}
    </div>
  );
}

export default NoteTimeline;
