import polymath_logo from "./images/polymath_logo.jpg";
import "./BootstrapNavbar.css";
import { useCallback, useEffect, useState } from "react";
import MediaQuery from "react-responsive";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import Offcanvas from "react-bootstrap/Offcanvas";
import Form from "react-bootstrap/Form";

function BootstrapNavbar() {
  const [isHeaderShown, setIsHeaderShown] = useState(true);
  const [lastPosition, setLastPosition] = useState(0);
  const headerHeight = 500;

  const scrollEvent = useCallback(() => {
    const offset = window.scrollY;
    console.log(offset);

    if (offset > headerHeight) {
      setIsHeaderShown(false);
    } else {
      setIsHeaderShown(true);
    }

    if (offset < lastPosition) {
      setIsHeaderShown(true);
    }

    setLastPosition(offset);
  }, [lastPosition]);

  useEffect(() => {
    window.addEventListener("scroll", scrollEvent);

    return () => {
      window.removeEventListener("scroll", scrollEvent);
    };
  }, [scrollEvent]);

  if (isHeaderShown) {
    return (
      <div>
        <MediaQuery query="(max-width: 767px)">
          <Navbar bg="light" expand="false" className="mb-3">
            <Container fluid>
              <Navbar.Brand href="#">Navbar Offcanvas</Navbar.Brand>
              <Navbar.Toggle
                aria-controls={`offcanvasNavbar-expand-false`}
              />
              <Navbar.Offcanvas
                id={`offcanvasNavbar-expand-false`}
                aria-labelledby={`offcanvasNavbarLabel-expand-false`}
                placement="end"
              >
                <Offcanvas.Header closeButton>
                  <Offcanvas.Title id={`offcanvasNavbarLabel-expand-false`}>
                    Offcanvas
                  </Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                  <Nav className="justify-content-end flex-grow-1 pe-3">
                    <Nav.Link href="#action1">Home</Nav.Link>
                    <Nav.Link href="#action2">Link</Nav.Link>
                    <NavDropdown
                      title="Dropdown"
                      id={`offcanvasNavbarDropdown-expand-false`}
                    >
                      <NavDropdown.Item href="#action3">
                        Action
                      </NavDropdown.Item>
                      <NavDropdown.Item href="#action4">
                        Another action
                      </NavDropdown.Item>
                      <NavDropdown.Divider />
                      <NavDropdown.Item href="#action5">
                        Something else here
                      </NavDropdown.Item>
                    </NavDropdown>
                  </Nav>
                  <Form className="d-flex">
                    <Form.Control
                      type="search"
                      placeholder="Search"
                      className="me-2"
                      aria-label="Search"
                    />
                    <Button variant="outline-success">Search</Button>
                  </Form>
                </Offcanvas.Body>
              </Navbar.Offcanvas>
            </Container>
          </Navbar>
        </MediaQuery>
        <MediaQuery query="(min-width: 767px)">
          <Navbar fixed="top" className="py-4 scroll-nav">
            <Container>
              <img src={polymath_logo} className="polymath_logo" />
              <Nav className="me-auto navbar">
                <Nav.Link href="#home">Home</Nav.Link>
                <Nav.Link href="#team">Team</Nav.Link>
                <Nav.Link href="#value">Value</Nav.Link>
                <Nav.Link href="#achievements">Achievments</Nav.Link>
                <Nav.Link href="#contact">Contact</Nav.Link>
                <Nav.Link href="#social">Social</Nav.Link>
              </Nav>
            </Container>
          </Navbar>
        </MediaQuery>
      </div>
    );
  }
}

export default BootstrapNavbar;
