import React, { useState } from "react";
import Form from "react-bootstrap/Form";

function Contact() {
  const [status, setStatus] = useState("Submit");

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("buttom pushed");
    setStatus("Sending...");
    const { name, email, message, options } = e.target.elements;

    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
      options: options.value,
    };

    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name </label>
        <input type="text" id="name" required />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" required />
      </div>
      <div>
        <label htmlFor="message">Message:</label>
        <textarea id="message" required />
      </div>
      <button type="submit">{status}</button>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Control type="textarea" placeholder="名前" htmlFor="name" />
        <Form.Control
          type="textarea"
          placeholder="メールアドレス"
          htmlFor="email"
        />
        <Form.Control type="textarea" placeholder="電話番号" htmlFor="tel" />
        <Form.Control
          type="textarea"
          placeholder="問い合わせ内容"
          htmlFor="message"
        />
        <Form.Select htmlFor="options">
          <option> 当サービスを知ったきっかけ*</option>
          <option value="1">Twitter</option>
          <option value="1">Facebook</option>
          <option value="1">Youtube</option>
          <option value="1">Note</option>
          <option value="1">友人</option>
          <option value="1">会社</option>
          <option value="1">大学</option>
          <option value="1">その他</option>
        </Form.Select>
      </Form.Group>
      <button type="submit"> {status} </button>
    </form>
  );
}

export default Contact;
