import React, { useState } from "react";
import Form from "react-bootstrap/Form";

function Contact() {
  const [status, setStatus] = useState("Submit");

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("buttom pushed");
    setStatus("Sending...");
    const { name, email, tel, message, options } = e.target.elements;

    let details = {
      name: name.value,
      email: email.value,
      tel: tel.value,
      message: message.value,
      options: options.value,
    };

    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <input type="text" id="name" placeholder="名前" required />
      </div>
      <div>
        <input type="email" id="email" placeholder="メールアドレス" required />
      </div>
      <div>
        <input type="text" id="tel" placeholder="電話番号" required />
      </div>
      <div>
        <input textarea id="message" placeholder="お問合せ内容" required />
      </div>
      <div>
        {/* <input textarea id="options" placeholder="当サービスを知ったきっかけ*" required /> */}
        <select id="options">
          <option value=""> 当サービスを知ったきっかけ*</option>
          <option value="Twitter">Twitter</option>
          <option value="Facebook">Facebook</option>
          <option value="Youtube">Youtube</option>
          <option value="Note">Note</option>
          <option value="友人">友人</option>
          <option value="1">会社</option>
          <option value="1">大学</option>
          <option value="1">その他</option>
        </select>
      </div>
      <button type="submit">{status}</button>
      {/* <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Control type="textarea" placeholder="名前" htmlFor="name" />
        <Form.Control
          type="textarea"
          placeholder="メールアドレス"
          htmlFor="email"
        />
        <Form.Control type="textarea" placeholder="電話番号" htmlFor="tel" />
        <Form.Control
          type="textarea"
          placeholder="問い合わせ内容"
          htmlFor="message"
        />
        <Form.Select htmlFor="options">
          <option> 当サービスを知ったきっかけ*</option>
